import { useState } from 'react';
import './App.css';

function App() {
  // 1. State for Form Data
  const [formData, setFormData] = useState({
    Age: '',
    Sex: '1',
    'Chest pain type': '',
    BP: '',
    Cholesterol: '',
    'FBS over 120': '0',
    'EKG results': '',
    'Max HR': '',
    'Exercise angina': '0',
    'ST depression': '',
    'Slope of ST': '',
    'Number of vessels fluro': '',
    Thallium: ''
  });

  // 2. State for Result
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // 3. Handle Input Change (Generic handler for all fields)
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  // 4. Handle Submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResult(null);

    try {
      // Send data to PyCharm Backend
      const response = await fetch('http://127.0.0.1:5000/predict', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error(`Server error: ${response.statusText}`);
      }

      const data = await response.json();
      setResult(data);

    } catch (err) {
      console.error(err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
      <div style={{ padding: '20px', maxWidth: '600px', margin: 'auto', fontFamily: 'Arial, sans-serif' }}>
        <h2 style={{ textAlign: 'center' }}>Heart Disease Predictor (React)</h2>

        {/* The Form */}
        <form onSubmit={handleSubmit} style={{ display: 'grid', gap: '10px', background: '#f9f9f9', padding: '20px', borderRadius: '8px' }}>

          <label>Age:
            <input type="number" name="Age" value={formData.Age} onChange={handleChange} required style={{ width: '100%' }} />
          </label>

          <label>Sex:
            <select name="Sex" value={formData.Sex} onChange={handleChange} style={{ width: '100%' }}>
              <option value="1">Male</option>
              <option value="0">Female</option>
            </select>
          </label>

          <label>Chest pain type:
            <input type="number" name="Chest pain type" value={formData['Chest pain type']} onChange={handleChange} required style={{ width: '100%' }} />
          </label>

          <label>BP:
            <input type="number" name="BP" value={formData.BP} onChange={handleChange} required style={{ width: '100%' }} />
          </label>

          <label>Cholesterol:
            <input type="number" name="Cholesterol" value={formData.Cholesterol} onChange={handleChange} required style={{ width: '100%' }} />
          </label>

          <label>FBS over 120:
            <select name="FBS over 120" value={formData['FBS over 120']} onChange={handleChange} style={{ width: '100%' }}>
              <option value="0">No</option>
              <option value="1">Yes</option>
            </select>
          </label>

          <label>EKG results:
            <input type="number" name="EKG results" value={formData['EKG results']} onChange={handleChange} required style={{ width: '100%' }} />
          </label>

          <label>Max HR:
            <input type="number" name="Max HR" value={formData['Max HR']} onChange={handleChange} required style={{ width: '100%' }} />
          </label>

          <label>Exercise angina:
            <select name="Exercise angina" value={formData['Exercise angina']} onChange={handleChange} style={{ width: '100%' }}>
              <option value="0">No</option>
              <option value="1">Yes</option>
            </select>
          </label>

          <label>ST depression:
            <input type="number" step="0.1" name="ST depression" value={formData['ST depression']} onChange={handleChange} required style={{ width: '100%' }} />
          </label>

          <label>Slope of ST:
            <input type="number" name="Slope of ST" value={formData['Slope of ST']} onChange={handleChange} required style={{ width: '100%' }} />
          </label>

          <label>Number of vessels fluro:
            <input type="number" name="Number of vessels fluro" value={formData['Number of vessels fluro']} onChange={handleChange} required style={{ width: '100%' }} />
          </label>

          <label>Thallium:
            <input type="number" name="Thallium" value={formData.Thallium} onChange={handleChange} required style={{ width: '100%' }} />
          </label>

          <button type="submit" disabled={loading} style={{ padding: '10px', background: '#007BFF', color: 'white', border: 'none', cursor: 'pointer', marginTop: '10px' }}>
            {loading ? 'Analyzing...' : 'Predict'}
          </button>
        </form>

        {/* The Result Display */}
        {error && <div style={{ color: 'red', marginTop: '20px', padding: '10px', border: '1px solid red' }}>Error: {error}</div>}

        {result && (
            <div style={{
              marginTop: '20px',
              padding: '15px',
              textAlign: 'center',
              fontWeight: 'bold',
              background: result.prediction === 1 ? '#ffdddd' : '#dff0d8',
              color: result.prediction === 1 ? '#a94442' : '#3c763d'
            }}>
              {result.prediction === 1
                  ? `High Risk Detected! (Confidence: ${result.probability}%)`
                  : `Low Risk Detected. (Confidence: ${result.probability}%)`
              }
            </div>
        )}
      </div>
  );
}

export default App;
