import { useState } from 'react';

function App() {
  const [formData, setFormData] = useState({
    Age: 55,
    Sex: '1',
    'Chest pain type': 3,
    BP: 120,
    Cholesterol: 200,
    'FBS over 120': '0',
    'EKG results': 0,
    'Max HR': 150,
    'Exercise angina': '0',
    'ST depression': 1.0,
    'Slope of ST': 2,
    'Number of vessels fluro': 0,
    Thallium: '3'
  });

  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResult(null);

    try {
      const response = await fetch('http://127.0.0.1:5000/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error(`Server error: ${response.statusText}`);

      const data = await response.json();
      setResult(data);
    } catch (err) {
      console.error(err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4 font-sans text-gray-800">
        <div className="bg-white p-8 rounded-lg shadow-xl w-full max-w-4xl">

          <div className="text-center mb-8">
            <h2 className="text-4xl font-bold text-blue-600 mb-2">Elite Heart Disease Predictor</h2>
            <p className="text-gray-500">Deep Neural Network Analysis • Real-time Inference</p>
          </div>

          <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">

            {/* SECTION 1: PATIENT VITALS */}
            <div className="md:col-span-2">
              <h3 className="text-lg font-semibold text-gray-700 border-b pb-2 mb-3">Patient Vitals</h3>
            </div>

            {[
              { label: 'Age', name: 'Age', type: 'number' },
              { label: 'Resting Blood Pressure (BP)', name: 'BP', type: 'number' },
              { label: 'Cholesterol (mg/dL)', name: 'Cholesterol', type: 'number' },
              { label: 'Max Heart Rate', name: 'Max HR', type: 'number' },
              { label: 'ST Depression', name: 'ST depression', type: 'number', step: '0.1' },
            ].map(field => (
                <div key={field.name} className="col-span-1">
                  <label className="block text-sm font-bold text-gray-700 mb-1">{field.label}</label>
                  <input
                      type={field.type}
                      name={field.name}
                      value={formData[field.name]}
                      onChange={handleChange}
                      step={field.step || "1"}
                      className="w-full border-gray-300 border rounded-md p-3 focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition"
                  />
                </div>
            ))}

            {/* SECTION 2: MEDICAL HISTORY */}
            <div className="md:col-span-2 mt-4">
              <h3 className="text-lg font-semibold text-gray-700 border-b pb-2 mb-3">Medical History & Symptoms</h3>
            </div>

            {/* Sex */}
            <div>
              <label className="block text-sm font-bold text-gray-700 mb-1">Sex</label>
              <select name="Sex" value={formData.Sex} onChange={handleChange} className="w-full border-gray-300 border rounded-md p-3">
                <option value="1">Male</option>
                <option value="0">Female</option>
              </select>
            </div>

            {/* Chest Pain */}
            <div>
              <label className="block text-sm font-bold text-gray-700 mb-1">Chest Pain Type</label>
              <input type="number" name="Chest pain type" value={formData['Chest pain type']} onChange={handleChange} className="w-full border-gray-300 border rounded-md p-3" />
              <p className="text-xs text-gray-500 mt-1">0: Typical Angina, 1: Atypical, 2: Non-Anginal, 3: Asymptomatic</p>
            </div>

            {/* FBS */}
            <div>
              <label className="block text-sm font-bold text-gray-700 mb-1">Fasting Blood Sugar > 120 mg/dL</label>
              <select name="FBS over 120" value={formData['FBS over 120']} onChange={handleChange} className="w-full border-gray-300 border rounded-md p-3">
                <option value="0">No {`<=`} 120</option>
                <option value="1">Yes (> 120)</option>
              </select>
            </div>

            {/* EKG */}
            <div>
              <label className="block text-sm font-bold text-gray-700 mb-1">Resting EKG Results</label>
              <input type="number" name="EKG results" value={formData['EKG results']} onChange={handleChange} className="w-full border-gray-300 border rounded-md p-3" />
            </div>

            {/* Exercise Angina */}
            <div>
              <label className="block text-sm font-bold text-gray-700 mb-1">Exercise Induced Angina</label>
              <select name="Exercise angina" value={formData['Exercise angina']} onChange={handleChange} className="w-full border-gray-300 border rounded-md p-3">
                <option value="0">No</option>
                <option value="1">Yes</option>
              </select>
            </div>

            {/* Slope */}
            <div>
              <label className="block text-sm font-bold text-gray-700 mb-1">Slope of ST Segment</label>
              <input type="number" name="Slope of ST" value={formData['Slope of ST']} onChange={handleChange} className="w-full border-gray-300 border rounded-md p-3" />
            </div>

            {/* Vessels */}
            <div>
              <label className="block text-sm font-bold text-gray-700 mb-1">Number of Major Vessels (Fluoroscopy)</label>
              <input type="number" name="Number of vessels fluro" value={formData['Number of vessels fluro']} onChange={handleChange} className="w-full border-gray-300 border rounded-md p-3" />
            </div>

            {/* Thallium */}
            <div>
              <label className="block text-sm font-bold text-gray-700 mb-1">Thallium Stress Test Result</label>
              <input type="number" name="Thallium" value={formData.Thallium} onChange={handleChange} className="w-full border-gray-300 border rounded-md p-3" />
            </div>

            {/* Submit Button */}
            <div className="md:col-span-2 mt-4">
              <button type="submit" disabled={loading}
                      className={`w-full text-white font-bold py-4 px-6 rounded-lg text-lg transition duration-300 shadow-lg 
                ${loading ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700 hover:shadow-xl'}`}
              >
                {loading ? 'Processing Neural Network...' : 'Predict Heart Disease Risk'}
              </button>
            </div>
          </form>

          {/* Result Box */}
          {error && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-6 py-4 rounded relative" role="alert">
                <strong className="font-bold">System Error: </strong>
                <span className="block sm:inline">{error}</span>
              </div>
          )}

          {result && (
              <div className={`p-6 rounded-lg border text-center font-bold text-2xl shadow-inner mt-4 ${
                  result.prediction === 1
                      ? 'bg-red-50 border-red-500 text-red-800 animate-pulse'
                      : 'bg-green-50 border-green-500 text-green-800'
              }`}>
                {result.prediction === 1
                    ? `⚠️ HIGH RISK DETECTED (Confidence: ${result.probability}%)`
                    : `✅ LOW RISK DETECTED (Confidence: ${result.probability}%)`
                }
              </div>
          )}
        </div>
      </div>
  );
}

export default App;
