import { useState } from 'react';

function App() {
  const [formData, setFormData] = useState({
    Age: 55,
    Sex: '1',
    'Chest pain type': 3,
    BP: 120,
    Cholesterol: 200,
    'FBS over 120': '0',
    'EKG results': 0,
    'Max HR': 150,
    'Exercise angina': '0',
    'ST depression': 1.0,
    'Slope of ST': 2,
    'Number of vessels fluro': 0,
    Thallium: '3'
  });

  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResult(null);

    try {
      const response = await fetch('http://127.0.0.1:5000/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error(`Server error: ${response.statusText}`);

      const data = await response.json();
      setResult(data);
    } catch (err) {
      console.error(err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Helper to determine risk color for the bar
  const getRiskColor = (prob) => {
    if (prob > 70) return 'bg-red-500';
    if (prob > 40) return 'bg-orange-400';
    return 'bg-emerald-500';
  };

  return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4 font-sans text-slate-800">

        {/* Main Card with Glassmorphism Effect */}
        <div className="bg-white/90 backdrop-blur-xl p-8 rounded-2xl shadow-2xl w-full max-w-5xl border border-white/50">

          {/* Header */}
          <div className="text-center mb-8">
            <div className="inline-block p-3 rounded-full bg-blue-100 text-blue-600 mb-3 shadow-sm">
              {/* Simple Heart Icon SVG */}
              <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8" fill="currentColor" viewBox="0 0 24 24"><path d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 21 5.42 21 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"/></svg>
            </div>
            <h2 className="text-4xl font-extrabold text-slate-900 mb-2 tracking-tight">VitalThrob Heart Disease AI</h2>
            <p className="text-slate-500 font-medium">Deep Neural Network Analysis • Real-time Diagnostic Support</p>
          </div>

          <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">

            {/* SECTION 1: PATIENT VITALS */}
            <div className="md:col-span-2">
              <h3 className="text-sm font-bold text-slate-400 uppercase tracking-wider border-b border-slate-200 pb-2 mb-4">
                <span className="text-blue-600 mr-2">01.</span>Patient Vitals
              </h3>
            </div>

            {[
              { label: 'Age (Years)', name: 'Age', type: 'number' },
              { label: 'Resting Blood Pressure (mmHg)', name: 'BP', type: 'number' },
              { label: 'Serum Cholesterol (mg/dL)', name: 'Cholesterol', type: 'number' },
              { label: 'Max Heart Rate Achieved (bpm)', name: 'Max HR', type: 'number' },
              { label: 'ST Depression Induced by Exercise', name: 'ST depression', type: 'number', step: '0.1' },
            ].map(field => (
                <div key={field.name} className="col-span-1 group">
                  <label className="block text-xs font-bold text-slate-600 mb-1 uppercase group-hover:text-blue-600 transition-colors">{field.label}</label>
                  <input
                      type={field.type}
                      name={field.name}
                      value={formData[field.name]}
                      onChange={handleChange}
                      step={field.step || "1"}
                      className="w-full bg-slate-50 border border-slate-200 text-slate-900 rounded-lg p-3 focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all shadow-sm"
                  />
                </div>
            ))}

            {/* SECTION 2: MEDICAL HISTORY */}
            <div className="md:col-span-2 mt-2">
              <h3 className="text-sm font-bold text-slate-400 uppercase tracking-wider border-b border-slate-200 pb-2 mb-4">
                <span className="text-blue-600 mr-2">02.</span>Medical History & Symptoms
              </h3>
            </div>

            {/* Sex */}
            <div className="col-span-1">
              <label className="block text-xs font-bold text-slate-600 mb-1 uppercase">Sex</label>
              <select name="Sex" value={formData.Sex} onChange={handleChange} className="w-full bg-slate-50 border border-slate-200 rounded-lg p-3 focus:ring-2 focus:ring-blue-500">
                <option value="1">Male</option>
                <option value="0">Female</option>
              </select>
            </div>

            {/* Chest Pain */}
            <div className="col-span-1">
              <label className="block text-xs font-bold text-slate-600 mb-1 uppercase">Chest Pain Type</label>
              <input type="number" name="Chest pain type" value={formData['Chest pain type']} onChange={handleChange} className="w-full bg-slate-50 border border-slate-200 rounded-lg p-3" />
            </div>

            {/* FBS */}
            <div className="col-span-1">
              <label className="block text-xs font-bold text-slate-600 mb-1 uppercase">Fasting Blood Sugar > 120 mg/dL</label>
              <select name="FBS over 120" value={formData['FBS over 120']} onChange={handleChange} className="w-full bg-slate-50 border border-slate-200 rounded-lg p-3">
                <option value="0">No {`<=`} 120</option>
                <option value="1">Yes (> 120)</option>
              </select>
            </div>

            {/* EKG */}
            <div className="col-span-1">
              <label className="block text-xs font-bold text-slate-600 mb-1 uppercase">Resting EKG Results</label>
              <input type="number" name="EKG results" value={formData['EKG results']} onChange={handleChange} className="w-full bg-slate-50 border border-slate-200 rounded-lg p-3" />
            </div>

            {/* Exercise Angina */}
            <div className="col-span-1">
              <label className="block text-xs font-bold text-slate-600 mb-1 uppercase">Exercise Induced Angina</label>
              <select name="Exercise angina" value={formData['Exercise angina']} onChange={handleChange} className="w-full bg-slate-50 border border-slate-200 rounded-lg p-3">
                <option value="0">No</option>
                <option value="1">Yes</option>
              </select>
            </div>

            {/* Slope */}
            <div className="col-span-1">
              <label className="block text-xs font-bold text-slate-600 mb-1 uppercase">Slope of ST Segment</label>
              <input type="number" name="Slope of ST" value={formData['Slope of ST']} onChange={handleChange} className="w-full bg-slate-50 border border-slate-200 rounded-lg p-3" />
            </div>

            {/* Vessels */}
            <div className="col-span-1">
              <label className="block text-xs font-bold text-slate-600 mb-1 uppercase">Fluoroscopy Colored Vessels</label>
              <input type="number" name="Number of vessels fluro" value={formData['Number of vessels fluro']} onChange={handleChange} className="w-full bg-slate-50 border border-slate-200 rounded-lg p-3" />
            </div>

            {/* Thallium */}
            <div className="col-span-1">
              <label className="block text-xs font-bold text-slate-600 mb-1 uppercase">Thallium Stress Test</label>
              <input type="number" name="Thallium" value={formData.Thallium} onChange={handleChange} className="w-full bg-slate-50 border border-slate-200 rounded-lg p-3" />
            </div>

            {/* Submit Button */}
            <div className="md:col-span-2 mt-4">
              <button type="submit" disabled={loading}
                      className={`w-full text-white font-bold py-4 px-6 rounded-xl text-lg transition-all duration-300 shadow-lg transform hover:-translate-y-1 
                ${loading ? 'bg-slate-400 cursor-not-allowed' : 'bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700'}`}
              >
                {loading ? 'Processing Neural Network...' : 'Predict Heart Disease Risk'}
              </button>
            </div>
          </form>

          {/* Error Box */}
          {error && (
              <div className="bg-red-50 border border-red-200 text-red-700 px-6 py-4 rounded-xl flex items-center justify-center gap-3" role="alert">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" /></svg>
                <div>
                  <strong className="font-bold block">Prediction Error</strong>
                  <span className="block sm:inline">{error}</span>
                </div>
              </div>
          )}

          {/* Result Section */}
          {result && (
              <div className="animate-fade-in-up mt-6">
                {/* Visual Risk Meter */}
                <div className="text-center mb-4">
                  <p className="text-slate-500 text-sm font-medium mb-2">Neural Network Confidence Score</p>
                  <div className="w-full bg-slate-200 rounded-full h-6 overflow-hidden relative">
                    <div
                        className={`h-full transition-all duration-1000 ease-out ${getRiskColor(result.probability)}`}
                        style={{ width: `${result.probability}%` }}
                    ></div>
                  </div>
                  <p className="text-3xl font-bold mt-2 text-slate-800">{result.probability}%</p>
                </div>

                {/* Result Badge */}
                <div className={`p-8 rounded-2xl border text-center shadow-inner ${
                    result.prediction === 1
                        ? 'bg-gradient-to-br from-red-50 to-red-100 border-red-200 text-red-900'
                        : 'bg-gradient-to-br from-emerald-50 to-emerald-100 border-emerald-200 text-emerald-900'
                }`}>
                  {result.prediction === 1
                      ? (
                          <>
                            <div className="text-5xl mb-2">⚠️</div>
                            <h3 className="text-3xl font-bold mb-2">High Risk Detected</h3>
                            <p className="text-sm opacity-80">Model suggests high probability of cardiovascular anomaly. Immediate medical consultation recommended.</p>
                          </>
                      )
                      : (
                          <>
                            <div className="text-5xl mb-2">✅</div>
                            <h3 className="text-3xl font-bold mb-2">Low Risk Detected</h3>
                            <p className="text-sm opacity-80">Model indicates low probability of cardiovascular disease based on provided metrics.</p>
                          </>
                      )}
                </div>

                {/* Download/Print Button */}
                <div className="text-center mt-6">
                  <button
                      onClick={() => window.print()}
                      className="text-sm text-slate-500 hover:text-blue-600 underline font-medium"
                  >
                    Print / Save Patient Report
                  </button>
                </div>
              </div>
          )}

          {/* Ethical Footer */}
          <div className="mt-8 border-t border-slate-200 pt-6 text-center">
            <p className="text-xs text-slate-400">
              ⚠️ <strong>Disclaimer:</strong> This tool is an AI educational demonstration.
              It does not constitute professional medical advice. Always consult a qualified doctor for health decisions.
            </p>
          </div>
        </div>
      </div>
  );
}

export default App;
